class Circulo:
    def __init__(self, raio):
        self.__area = 0
        self.__perimetro = 0
        self.__raio = raio
        self.__PI = 3.14
    def Area(self) -> float:
        return self.__PI * self.__raio * self.__raio

    def Perimetro(self) -> float:
        return  2 * self.__PI * self.__raio


ask = int(input("Qual raio do circulo?"))

new_instance = Circulo(ask)

print(f"{new_instance.Area():.2f}")
print(f"{new_instance.Perimetro():.2f}")

